// Add your custom debug buttons here
Level_l.prototype.customDebugButtons = function(tag){
	switch(tag){
	case 'Game Over':
		console.log('GAME OVER PRESSED');
		break;
	case 'Level 1':
		console.log('LEVEL 1 PRESSED');
		break;
	default: 
		console.log('No code for this tag');
	}
};

